#!/bin/bash\n\n# Variables\nWP_PATH='/path/to/wordpress'\nBACKUP_DIR='/path/to/backups'\nDATE=$(date +%Y%m%d_%H%M%S)\nZIPFILE="$BACKUP_DIR/db_backup_$DATE.zip"\nDB_BACKUP_FILE="$BACKUP_DIR/db_backup_$DATE.sql"\n\n# Create backup using wp-cli\nwp db export $DB_BACKUP_FILE --path=$WP_PATH\n\n# Zip the backup file\nzip -j "$ZIPFILE" "$DB_BACKUP_FILE"\n\n# Send the zip via email (make sure mailutils or similar is installed and configured)\necho "WordPress Database Backup for $(date)" | mutt -a "$ZIPFILE" -s "WordPress DB Backup" -- admin@example.com\n\n# Optional: Remove the backup file to save space\nrm $DB_BACKUP_FILE