#!/bin/bash\n# This script should be run as a cron job or similar to forward emails\n\nwhile IFS= read -r email; do\n    # Extract identifier part from the address\n    identifier=$(echo "$email" | cut -d'-' -f1)\n    \n    # Retrieve user's real email from database using identifier\n    real_email=$(php -r "getRealEmail('$identifier');")\n    \n    # Forward email if a valid real_email is found\n    if [ ! -z "$real_email" ]; then\n        echo "$email" | mail -s "Forwarded Email" $real_email\n    fi\ndone < /tmp/incoming_emails.txt