#!/bin/bash\n\n# Check if source-highlight and convert are installed\nif ! command -v source-highlight &> /dev/null || ! command -v convert &> /dev/null; then\n    echo "source-highlight and/or ImageMagick's convert not found. Please install them."\n    exit 1\nfi\n\ninput_file="path/to/your/sourcefile.java"\ntemp_pdf="/tmp/highlighted_source.pdf"\n\n# Generate a pretty-printed PDF using source-highlight\nsource-highlight -f pdf -i "${input_file}" -o "${temp_pdf}"\n\n# Extract number of pages and loop through each page to convert to JPEG\npage_count=$(pdfinfo "${temp_pdf}" | grep Pages | awk '{print $2}')\nfor ((page_number=1; page_number<=page_count; page_number++)); do\n    output_jpeg="/path/to/save/output_${page_number}.jpeg"\n    # Convert each PDF page to a cropped and resized JPEG\n    convert -density 300 "${temp_pdf}[${page_number}-1]" \\n            -quality 90 \\n            -crop 575x148+5+28 +repage \\n            "${output_jpeg}"\ndone\n\n# Clean up temporary PDF file if not needed anymore\nrm "${temp_pdf}"