#!/bin/bash\n\n# Check if exactly one argument is passed.\nif [ "$#" -ne 1 ]; then\n    echo "Error: Exactly one argument required."\n    exit 1\nfi\n\n# Check if the argument is a number.\nif ! [[ "$1" =~ ^[0-9]+$ ]]; then\n    echo "Error: Argument must be a number."\n    exit 1\nfi\n\nID="$1"\nFOLDERS=(\n  "~/myfolder1/$ID/anotherfolder"\n  "~/myfolder2/$ID/yetanotherfolder"\n  "~/myfolder3/$ID/thisisafolder"\n)\n\n# Check if each folder exists before attempting to delete it.\nfor FOLDER in "${FOLDERS[@]}"; do\n    if [ -d "$FOLDER" ]; then\n        rm -rf "$FOLDER"\n        echo "Deleted $FOLDER"\n    else\n        echo "Folder $FOLDER does not exist."\n    fi\ndone