#!/bin/bash\n\n# Variables\nREPOS="http://svn.example.com/project/trunk"\nCHECKOUT_DIR="/srv/www/checkouts/project"\nLIVE_DIR="/var/www/project"\n\n# Update the checkout directory\nif [ -d "$CHECKOUT_DIR" ]; then\n    svn update $CHECKOUT_DIR\nelse\n    svn checkout $REPOS $CHECKOUT_DIR\nfi\n\n# Remove any local changes that might not have been committed\nsvn revert -R $CHECKOUT_DIR 2>/dev/null\n\n# Optionally, perform a build or other pre-deployment task here\n# cd $CHECKOUT_DIR && make all\n\n# Deploy the updated working copy to the live directory\nrsync -av --delete-after $CHECKOUT_DIR/ $LIVE_DIR/\n\n# Set correct permissions (consider this step carefully)\nchown -R apache:apache $LIVE_DIR\nfind $LIVE_DIR -type d -exec chmod 755 {} \;\nfind $LIVE_DIR -type f -exec chmod 644 {} \;\n\necho "Deployment complete."