#!/bin/bash\n\n# Define the start and end times\nSTART_TIME="May  3 13:20:00"\nEND_TIME="May  3 13:30:00"\n\n# Function to grep between two timestamps in a file\ngrep_between_timestamps() {\n    local file="$1"\n    awk -v start="$START_TIME" -v end="$END_TIME" '\n        $0 >= start && found_start == 0 {found_start=1}\n        found_start == 1 && $0 <= end'\n}\n\n# Process each log file\nfor LOGFILE in mail.log mail.log.0; do\n    echo "Processing $LOGFILE"\n    grep_between_timestamps "$LOGFILE"\ndone\n\n# Process compressed log files\nfor LOGFILE in mail.log.1.gz mail.log.2.gz; do\n    echo "Processing $LOGFILE"\n    zcat "$LOGFILE" | grep_between_timestamps\ndone