#!/bin/bash\n\n# Replace FILE_PATH with the path to your file\nFILE_PATH='/path/to/your/file'\n\n# Get the inode of the file\nINODE=$(ls -i "$FILE_PATH" | awk '{print $1}')\n\n# Find and close all open file descriptors associated with this inode\nif [ -n "$INODE" ]; then\n    lsof | grep " $INODE " | awk '{print "/proc/" $2 "/fd/" $4}' | xargs -I {} sh -c 'exec 3<{}; exec 3<&-'\nfi