BACKUP_DIR="/path/to/backup"\nREPO_PATH="/path/to/svn/repo"\nFULL_BACKUP_FILE="$BACKUP_DIR/full_backup_$(date +%Y%m%d).dump"\nINCREMENTAL_BACKUP_FILE="$BACKUP_DIR/incremental_backup_$(date +%Y%m%d%H%M%S).dump"\n\n# Perform a full backup if it's the first day of the week\nif [ $(date +%u) -eq 1 ]; then\n    svnadmin dump $REPO_PATH > "$FULL_BACKUP_FILE"\nelse\n    # Otherwise, perform an incremental backup based on the last full backup\n    LAST_FULL_BACKUP=$(ls $BACKUP_DIR/full_backup_*.dump | tail -n 1)\n    REVISION_START=$(( $(svnlook youngest $REPO_PATH) + 1 ))\n    if [ -f "$LAST_FULL_BACKUP" ]; then\n        # Get the revision number from the filename and add 1 as a starting point for incrementals\n        START_REVISION=$(basename "${LAST_FULL_BACKUP}" .dump | cut -d'_' -f3)\n        svnadmin dump --incremental -r $START_REVISION:HEAD $REPO_PATH > "$INCREMENTAL_BACKUP_FILE"\n    fi\nfi\n\n# Optionally, compress the backup files to save space\ngzip "$FULL_BACKUP_FILE"\ngzip "$INCREMENTAL_BACKUP_FILE"