using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    static void Main()\n    {\n        // ProgID for Excel 9 (Office 2000)\n        string excelProgId = "Excel.Application.9";\n\n        Type excelType = Type.GetTypeFromProgID(excelProgId);\n        if (excelType == null)\n        {\n            throw new COMException("Excel 9 is not installed on this machine.");\n        }\n\n        var excelApplication = Activator.CreateInstance(excelType) as Microsoft.Office.Interop.Excel.Application;\n        if (excelApplication != null)\n        {\n            excelApplication.Visible = true; // Make Excel visible for testing\n            // Use the application object here...\n        }\n    }\n}