using System.Data;\n  using System.Diagnostics;\n\n  public class OracleHelper\n  {\n      private static void LogQuery(string sql)\n      {\n          // Simple logging, could be enhanced with more details\n          Debug.WriteLine(sql);\n      }\n\n      public static DataTable ExecuteQuery(string connectionString, string query, params object[] parameters)\n      {\n          LogQuery(query); // Log the query before execution\n          \n          using (var connection = new OracleConnection(connectionString))\n          {\n              using (var command = new OracleCommand(query, connection))\n              {\n                  if (parameters != null && parameters.Length > 0)\n                  {\n                      for (int i = 0; i < parameters.Length; i++)\n                          command.Parameters.AddWithValue("p" + i, parameters[i]);\n                  }\n            \n                  connection.Open();\n                  using (var reader = command.ExecuteReader())\n                  {\n                      var dataTable = new DataTable();\n                      dataTable.Load(reader);\n                      return dataTable;\n                  }\n              }\n          }\n      }\n  }