using System;\nusing System.Web;\n\npublic class SpoofRefererExample : IHttpHandler {\n    public void ProcessRequest(HttpContext context)\n    {\n        // For demonstration, I'm setting a custom Referer header value.\n        string spoofedReferer = "http://www.spoofed-referer.com";\n        \n        if (string.Equals(spoofedReferer, context.Request.UrlReferrer?.AbsoluteUri, StringComparison.OrdinalIgnoreCase))\n        {\n            context.Response.Write("Request is coming from the right page.");\n        }\n        else\n        {\n            context.Response.Write("Request is not authentic.");\n        }\n    }\n\n    public bool IsReusable => false;\n}