[Test]\npublic void Test_Surname_Setter_Correctly_Assigns_Value()\n{\n    // Arrange\n    var person = new Person();\n\n    // Act\n    person.Surname = "Doe";\n\n    // Assert\n    Assert.AreEqual("Doe", person.Surname);\n}\n\n[Test]\npublic void Test_Surname_Getter_Returns_Previously_Set_Value()\n{\n    // Arrange\n    var person = new Person();\n    person.Surname = "Doe"; // Set the value first\n\n    // Act\n    string surname = person.Surname;\n\n    // Assert\n    Assert.AreEqual("Doe", surname);\n}\n\n// Example of a test for validation logic (if added later)\n[Test]\npublic void Test_Surname_Throws_Exception_on_Invalid_Value()\n{\n    // Arrange\n    var person = new Person();\n\n    // Act & Assert\n    Assert.Throws<ArgumentException>(() => person.Surname = "");\n}