using System;\nusing System.IO;\nusing System.Net;\n\nclass Program\n{\n    static void Main()\n    {\n        for (int i = 0; i < 3; i++)\n        {\n            string strPageNo = Convert.ToString(i);\n            string strURL = "http://www.website.com/results.aspx?page=" + strPageNo;\n\n            HttpWebRequest wrGETURL = (HttpWebRequest)WebRequest.Create(strURL);\n\n            // Set the User-Agent\n            wrGETURL.UserAgent = "Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/58.0.3029.110 Safari/537.3";\n            \n            // Disable caching\n            wrGETURL.CachePolicy = new System.Net.Cache.RequestCachePolicy(System.Net.Cache.RequestCacheLevel.NoCacheNoStore);\n\n            HttpWebResponse response = (HttpWebResponse)wrGETURL.GetResponse();\n            Stream objStream = response.GetResponseStream();\n\n            StreamReader objReader = new StreamReader(objStream);\n            string result = objReader.ReadToEnd(); // Read the content\n\n            // Save 'result' to a file or process it as needed\n            Console.WriteLine(result);\n\n            objStream.Close();\n            objReader.Close();\n            response.Close();\n\n            System.Threading.Thread.Sleep(1000); \n        }\n    }\n}