using System;\nusing System.Diagnostics;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        ProcessStartInfo start = new ProcessStartInfo();\n        start.FileName = "your_external_program.exe";\n        start.UseShellExecute = false;\n        // Create a pipe to send the commands to the child process.\n        using (AnonymousPipeClientStream pipeToChild = \n               new AnonymousPipeClientStream(PipeDirection.Out, "4f81e69b-2770-43ca-a52d-a58645cbeaac"))\n        {\n            // Use this stream to send commands to the child process\n            StreamWriter childWriter = new StreamWriter(pipeToChild);\n\n            start.RedirectStandardInput = true;\n            \n            using (Process p = Process.Start(start))\n            {\n                // Write commands through the pipe\n                for (int i = 0; i < 10; i++)\n                {\n                    Console.WriteLine("Type CNT to continue: ");\n                    string command = Console.ReadLine();\n                    if (command == "CNT")\n                    {\n                        childWriter.WriteLine(command);\n                        childWriter.Flush();\n                    }\n                }\n\n                p.WaitForExit();\n            } // Release the pipe handle here as well.\n        }\n    }\n}