using System;\nusing System.EnterpriseServices.Administration;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string remoteServer = "RemoteServerName";\n        string comPackageName = "YourComApplicationName";\n\n        try\n        {\n            // Connect to the COM+ Administration catalog on the remote server\n            using (Catalog catalog = new Catalog())\n            {\n                // Set up a connection to the remote server\n                IntPtr machineMoniker = Moniker.GetObject(string.Format("Remote-{0}", remoteServer));\n                object appCollectionVar = catalog.GetCollectionFromMachine(machineMoniker, "Applications");\n                CatalogCollection applications = (CatalogCollection)appCollectionVar;\n\n                // Load all applications\n                applications.Populate();\n\n                // Find the specific COM+ application\n                foreach (object o in (System.Collections.IEnumerable)applications)\n                {\n                    CatalogObject application = (CatalogObject)o;\n                    if (application.Name == comPackageName)\n                    {\n                        Console.WriteLine($"Stopping {comPackageName}...");\n                        applications.SetMultipleComponentsProperty("Stop", 0, new object[] { application.Key });\n\n                        // Wait a moment to ensure it stops\n                        System.Threading.Thread.Sleep(2000);\n\n                        Console.WriteLine($"Starting {comPackageName}...");\n                        applications.SetMultipleComponentsProperty("Start", 0, new object[] { application.Key });\n\n                        Console.WriteLine($"{comPackageName} restarted successfully on {remoteServer}.");\n                        break;\n                    }\n                }\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"Failed to restart COM+ application: {ex.Message}");\n        }\n    }\n}