public static class DataFormSubject\n{\n    private static List<DataForm> _dataForms = new List<DataForm>();\n\n    public delegate void DataFormsUpdatedHandler(object sender);\n    public static event DataFormsUpdatedHandler DataFormsUpdatedEvent;\n\n    public static void AddDataForm(DataForm df)\n    {\n        _dataForms.Add(df);\n        OnDataFormsUpdated();\n    }\n\n    public static void RemoveDataForm(DataForm df)\n    {\n        _dataForms.Remove(df);\n        OnDataFormsUpdated();\n    }\n\n    private static void OnDataFormsUpdated()\n    {\n        DataFormsUpdatedEvent?.Invoke(null); // Use null or appropriate sender\n    }\n\n    public static IReadOnlyList<DataForm> DataForms\n    {\n        get { return _dataForms.AsReadOnly(); }\n    }\n}