using System;\nusing System.ComponentModel;\nusing System.Drawing;\nusing System.Windows.Forms;\n\n[ToolboxItem(true)]\npublic class SampleControl : Control\n{\n    private string _sampleText = "Default Text";\n\n    [Category("Appearance")]\n    [Description("The text to display on the control.")]\n    public string SampleText\n    {\n        get { return _sampleText; }\n        set \n        { \n            _sampleText = value;\n            Invalidate(); // Redraw the control when the property changes\n        }\n    }\n\n    protected override void OnPaint(PaintEventArgs e)\n    {\n        base.OnPaint(e);\n        e.Graphics.DrawString(SampleText, Font, Brushes.Black, new PointF(0, 0));\n    }\n}