using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string path = @"\\fors343a.ww123.somedomain.net\folder\1";\n\n        if (IsUncPath(path))\n        {\n            Console.WriteLine("This is a UNC path.");\n            try\n            {\n                var directoryInfo = new DirectoryInfo(path);\n                // Access directory information here, e.g., directoryInfo.Exists\n                if (directoryInfo.Exists)\n                {\n                    Console.WriteLine("Directory exists on the network drive.");\n                }\n                else\n                {\n                    Console.WriteLine("Directory does not exist.");\n                }\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine($"Error accessing path: {ex.Message}");\n            }\n        }\n        else\n        {\n            Console.WriteLine("This is a local path.");\n            try\n            {\n                var driveInfo = new DriveInfo(Path.GetPathRoot(path));\n                // Access drive information here, e.g., driveInfo.AvailableFreeSpace\n                if (driveInfo.IsReady)\n                {\n                    Console.WriteLine($"Drive {driveInfo.Name} is ready.");\n                }\n                else\n                {\n                    Console.WriteLine($"Drive {driveInfo.Name} is not ready.");\n                }\n            }\n            catch (ArgumentException ex)\n            {\n                Console.WriteLine($"Invalid drive path: {ex.Message}");\n            }\n        }\n    }\n\n    static bool IsUncPath(string path)\n    {\n        return Path.IsPathFullyQualified(path) && path.StartsWith(@"\\");\n    }\n}