using System;\nusing System.Windows.Forms;\n\npublic enum ModifierKeys\n{\n    MOD_NONE = 0x0000,\n    MOD_ALT = 0x0001,\n    MOD_CONTROL = 0x0002,\n    MOD_SHIFT = 0x0004,\n    // Add more as needed\n}\n\npublic class OptionsForm : Form\n{\n    private ComboBox keyComboBox;\n    private CheckBox controlCheckBox;\n    private CheckBox shiftCheckBox;\n\n    public void InitializeComponents()\n    {\n        // Setup your ComboBox and CheckBoxes here\n\n        Button registerButton = new Button();\n        registerButton.Text = "Register Hotkey";\n        registerButton.Click += RegisterHotKey_Click;\n        \n        Controls.Add(registerButton);\n    }\n\n    private void RegisterHotKey_Click(object sender, EventArgs e)\n    {\n        if (Enum.TryParse(keyComboBox.SelectedItem.ToString(), true, out Keys hotkey))\n        {\n            int modifiers = 0;\n\n            if (controlCheckBox.Checked)\n                modifiers |= (int)ModifierKeys.MOD_CONTROL;\n            if (shiftCheckBox.Checked)\n                modifiers |= (int)ModifierKeys.MOD_SHIFT;\n\n            RegisterGlobalHotKey(hotkey, modifiers);\n        }\n        else\n        {\n            MessageBox.Show("Invalid key selection.");\n        }\n    }\n\n    private void RegisterGlobalHotKey(Keys hotkey, int modifiers)\n    {\n        // Your existing code to register the global hotkey goes here\n    }\n}