using System;\nusing System.Web.UI;\n\npublic class SessionAwarePage : Page\n{\n    protected override void PreRenderComplete(EventArgs e)\n    {\n        base.PreRenderComplete(e);\n        // Capture the original HTML output\n        var writer = new System.IO.StringWriter();\n        using (var htmlWriter = new HtmlTextWriter(writer))\n        {\n            this.RenderControl(htmlWriter);\n        }\n\n        // Modify the HTML output here, for example, add session info to links\n        string modifiedHtml = AddSessionInfoToLinks(writer.ToString());\n\n        // Write back the modified HTML\n        Response.Clear();\n        Response.Write(modifiedHtml);\n    }\n\n    private string AddSessionInfoToLinks(string html)\n    {\n        // Simple placeholder for adding session info.\n        // This example just appends a querystring to all anchor tags, but you'd replace this with your actual logic.\n        return html.Replace("<a ", "<a data-my-session-info=\"12345\" ");\n    }\n}