using System;\nusing xfinium.pdf;\nusing xfinium.pdf.graphics;\nusing xfinium.pdf.text;\n\npublic class PdfGenerator\n{\n    public void CreatePdf()\n    {\n        // Create the document.\n        PdfDocument pdf = new PdfDocument();\n\n        // Add a page to the document.\n        PdfPage pdfPage = pdf.AddPage();\n\n        // Graphics for drawing content in the PDF.\n        PdfGraphics g = pdfPage.Graphics;\n\n        // Set font used for drawing text in the PDF.\n        PdfStringFormat format = new PdfStringFormat(PdfTextAlignment.Near, PdfVerticalTextAlignment.Top);\n\n        // Draw a string inside the specified bounds.\n        g.DrawString("Hello, World!", new PdfStandardFont(PdfStandardFontFace.Helvetica, 24),\n            new PdfSolidBrush(new PdfRgbColor(0x3f, 0x3f, 0xff)), \n            xfinium.pdf.PdfRectangle.FromLTRB(100, 50, 792 - 100, 100 + 100),\n            format);\n\n        // Save the document to a stream or file.\n        pdf.Save("output.pdf");\n    }\n}