protected void BindImages(string[] imageurls)\n{\n    // Ensure all URLs are absolute\n    for (int i = 0; i < imageurls.Length; i++)\n    {\n        if (!Uri.IsWellFormedUriString(imageurls[i], UriKind.Absolute))\n        {\n            // Convert to absolute path based on your application's root\n            imageurls[i] = ResolveUrl(imageurls[i]);\n        }\n    }\n\n    dlImages.DataSource = imageurls;\n    dlImages.DataBind();\n}