using System;\nusing System.Data.OleDb;\n\npublic void ExportToAccess()\n{\n    string connectionString = "YourConnectionStringHere";\n    using (OleDbConnection connection = new OleDbConnection(connectionString))\n    {\n        try\n        {\n            connection.Open();\n            // Increase CommandTimeout to a higher value in seconds\n            connection.CommandTimeout = 120; // Set timeout to 120 seconds\n\n            // Your existing code to call doCmd.TransferDatabase here\n            // Example:\n            // accessApplication.DoCmd.TransferDatabase(Access.AcDataTransferType.acImport, "ODBC Database", connectionString, Access.AcObjectType.acTable, "SourceViewName", "DestinationTableName", false);\n\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("An error occurred: " + ex.Message);\n        }\n        finally\n        {\n            connection.Close();\n        }\n    }\n}