using System;\nusing System.Collections.Generic;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string rootPath = @"C:\"; // Specify the drive or directory to search\n        List<string> fileExtensions = new List<string>() { ".xml", ".csv", ".xls" }; // File extensions you want to find\n\n        List<string> foundFiles = SearchFiles(rootPath, fileExtensions);\n\n        foreach (string filePath in foundFiles)\n        {\n            Console.WriteLine(filePath);\n        }\n    }\n\n    static List<string> SearchFiles(string rootPath, List<string> fileExtensions)\n    {\n        List<string> results = new List<string>();\n\n        try\n        {\n            string[] files = Directory.GetFiles(rootPath);\n\n            foreach (string file in files)\n                if (fileExtensions.Contains(Path.GetExtension(file).ToLower()))\n                    results.Add(file);\n\n            string[] subDirectories = Directory.GetDirectories(rootPath);\n\n            foreach (string dir in subDirectories)\n                results.AddRange(SearchFiles(dir, fileExtensions));\n        }\n        catch (UnauthorizedAccessException e)\n        {\n            Console.WriteLine($"Access denied: {e.Message}");\n        }\n        catch (DirectoryNotFoundException e)\n        {\n            Console.WriteLine($"Directory not found: {e.Message}");\n        }\n        catch (PathTooLongException e)\n        {\n            Console.WriteLine($"Path too long: {e.Message}");\n        }\n        catch (IOException e)\n        {\n            Console.WriteLine($"IO Exception: {e.Message}");\n        }\n\n        return results;\n    }\n}