// Assume the VB6 project is named MyVB6Project\n// and has a class called MyClass with a Method MyMethod\n\nusing System;\nusing MyVB6Project;\n\nnamespace MyDotNetLibrary\n{\n    public class MyDotNetClass\n    {\n        public void CallVb6Method()\n        {\n            // Create an instance of the VB6 class\n            MyClass vb6Object = new MyClass();\n            \n            // Call a method from the VB6 object\n            vb6Object.MyMethod();\n        }\n    }\n}