using System.Data.SqlServerCe;\n\nstring connectionString = "YourConnectionStringHere";\nstring tableNameToCheck = "YourTableName";\n\nbool doesTableExist = false;\n\nusing (SqlCeConnection conn = new SqlCeConnection(connectionString))\n{\n    conn.Open();\n\n    string query = $"SELECT COUNT(*) FROM INFORMATION_SCHEMA.TABLES WHERE TABLE_NAME = '{tableNameToCheck}'";\n    using (SqlCeCommand cmd = new SqlCeCommand(query, conn))\n    {\n        doesTableExist = (long)cmd.ExecuteScalar() > 0;\n    }\n    \n    // Note: The above uses INFORMATION_SCHEMA which is not available in SQLCE.\n    // Instead, use the following query specific to SQL Server CE:\n\n    query = $"SELECT COUNT(*) FROM sys.tables WHERE table_name = '{tableNameToCheck}'";\n    using (SqlCeCommand cmd = new SqlCeCommand(query, conn))\n    {\n        doesTableExist = (long)cmd.ExecuteScalar() > 0;\n    }\n\n    Console.WriteLine($"Table {tableNameToCheck} exists: {doesTableExist}");\n}