protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        // Assuming MyUserControl is the ID of your ASCX user control on the page.\n        var myUserControl = this.FindControl("MyUserControl") as YourNamespace.YourUserControl;\n\n        if (myUserControl != null && myUserControl.YourDropDown != null)\n        {\n            RequiredFieldValidator validator = new RequiredFieldValidator\n            {\n                ID = "YourRequiredValidator",\n                ControlToValidate = myUserControl.YourDropDown.ID,\n                Text = "This field is required.",\n                ForeColor = System.Drawing.Color.Red,\n                ValidationGroup = "MyValidationGroup" // Optional, if different.\n            };\n\n            ValidatorPlaceholder.Controls.Add(validator); // Add to a container on your page.\n        }\n    }\n}