using System;\nusing System.Data;\nusing System.Data.SqlClient;\n\npublic class StoredProcedureParameterInfo\n{\n    public static void GetStoredProcedureParameters(string connectionString, string procedureName)\n    {\n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            connection.Open();\n\n            string query = @"\n                SELECT \n                    PARAMETER_NAME,\n                    DATA_TYPE,\n                    CHARACTER_MAXIMUM_LENGTH,\n                    IS_NULLABLE,\n                    PARAMETER_MODE\n                FROM \n                    INFORMATION_SCHEMA.PARAMETERS\n                WHERE \n                    SPECIFIC_NAME = @ProcedureName";\n\n            using (SqlCommand command = new SqlCommand(query, connection))\n            {\n                command.Parameters.AddWithValue("@ProcedureName", procedureName);\n\n                using (SqlDataReader reader = command.ExecuteReader())\n                {\n                    while (reader.Read())\n                    {\n                        Console.WriteLine($"Parameter Name: {reader["PARAMETER_NAME"]}, " +\n                                          $"Data Type: {reader["DATA_TYPE"]}, " +\n                                          $"Max Length: {reader["CHARACTER_MAXIMUM_LENGTH"]}, " +\n                                          $"Nullable: {reader["IS_NULLABLE"]}, " +\n                                          $"Mode: {reader["PARAMETER_MODE"]}");\n                    }\n                }\n            }\n        }\n    }\n\n    // Example usage\n    public static void Main()\n    {\n        string connectionString = "your_connection_string";\n        string procedureName = "YourProcedureName";\n\n        GetStoredProcedureParameters(connectionString, procedureName);\n    }\n}