using System;\nusing System.DirectoryServices;\n\n// Your existing searcher setup\nDirectorySearcher searcher = new DirectorySearcher();\nsearcher.Filter = String.Format("(SAMAccountName={0})", "TestA33");\nsearcher.PropertiesToLoad.Add("accountExpires");\n\nSearchResult result = searcher.FindOne();\nif (result != null)\n{\n    DirectoryEntry uEntry = result.GetDirectoryEntry();\n\n    // Get the accountExpires value\n    if (uEntry.Properties["accountExpires"].Value != null)\n    {\n        long? expiresVal = (long?)uEntry.Properties["accountExpires"].Value;\n        DateTime expiryDate;\n\n        // Check for never expire condition\n        if (expiresVal.HasValue && expiresVal.Value != 0)\n        {\n            // Convert the value to a DateTime\n            const long fileTimeOffset = (621355968000000000L); // Offset between FILETIME and .NET ticks\n            expiryDate = DateTime.FromFileTimeUtc(expiresVal.Value);\n        }\n        else\n        {\n            // If the value is 0, the account never expires\n            expiryDate = DateTime.MaxValue;\n        }\n\n        Response.Write(expiryDate.ToString());\n    }\n    else\n    {\n        Response.Write("accountExpires property not found.");\n    }\n}\nelse\n{\n    Response.Write("User not found.");\n}