using System;\nusing System.Runtime.InteropServices;\nusing System.Security.Principal;\n\npublic class SidToUsernameResolver\n{\n    public static string GetDisplayName(string sidString)\n    {\n        try\n        {\n            SecurityIdentifier sid = new SecurityIdentifier(sidString);\n            NTAccount account = (NTAccount)sid.Translate(typeof(NTAccount));\n            return account.Value;\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("An error occurred: " + ex.Message);\n            return null;\n        }\n    }\n\n    public static void Main(string[] args)\n    {\n        string sidString = "S-1-5-21-3623811015-3361044348-30300820-1013"; // Example SID\n        string displayName = GetDisplayName(sidString);\n        \n        if (displayName != null)\n        {\n            Console.WriteLine("Display Name: " + displayName);\n        }\n    }\n}