using System;\nusing System.Collections.Generic;\nusing System.Linq;\nusing System.Text.RegularExpressions;\n\nclass Program\n{\n    static void Main()\n    {\n        string sql = @"Update Employees\nSet FirstName = 'Jim', [LastName] = 'Smith', CodeNum = codes.Num\nFrom Employees as em\nJoin CodeNumbers as codes on codes.EmployeeID = em.EmployeeID";\n\n        var columns = ExtractColumns(sql);\n        foreach (var column in columns)\n        {\n            Console.WriteLine(column);\n        }\n    }\n\n    static IEnumerable<string> ExtractColumns(string sql)\n    {\n        // Regex to match columns after SET and before FROM\n        string pattern = @"(?<=SET\s)(.*?)(?=\s*FROM)";\n        var setClauseMatch = Regex.Match(sql, pattern, RegexOptions.IgnoreCase | RegexOptions.Singleline);\n\n        if (!setClauseMatch.Success) return Enumerable.Empty<string>();\n\n        string setClause = setClauseMatch.Groups[1].Value;\n\n        // Regex to match individual columns\n        pattern = @"[\[(]?(\b\w+\b)[\]]?\s*=";\n        var columnMatches = Regex.Matches(setClause, pattern);\n\n        return columnMatches.Cast<Match>().Select(m => m.Groups[1].Value);\n    }\n}