[HttpPost]\n    public IActionResult SavePerson(Person person)\n    {\n        // Add validation and other business logic here\n\n        if (ModelState.IsValid)\n        {\n            // Save to database\n            _personRepository.Add(person);\n            return RedirectToAction("Index");\n        }\n\n        return View(person); // Return the same view with model errors if any\n    }