using System;\nusing System.Collections.Generic;\n\npublic class TreeNode\n{\n    public string Name { get; set; }\n    public int Id { get; set; }\n    public List<TreeNode> Children { get; set; }\n\n    public TreeNode(string name, int id)\n    {\n        Name = name;\n        Id = id;\n        Children = new List<TreeNode>();\n    }\n}\n\npublic class TreeTraversal\n{\n    public static void TraverseRecursive(TreeNode node)\n    {\n        if (node == null) return;\n\n        // Print the current node's ID and Name\n        Console.WriteLine($"ID: {node.Id}, Name: {node.Name}");\n\n        // Recursively traverse each child\n        foreach (var child in node.Children)\n        {\n            TraverseRecursive(child);\n        }\n    }\n\n    public static void Main(string[] args)\n    {\n        // Example tree creation\n        TreeNode root = new TreeNode("Root", 1);\n        root.Children.Add(new TreeNode("Child1", 2));\n        root.Children[0].Children.Add(new TreeNode("Grandchild1", 3));\n        root.Children.Add(new TreeNode("Child2", 4));\n\n        TraverseRecursive(root);\n    }\n}