using System;\nusing System.Collections.Generic;\nusing System.Net.Http;\nusing System.Threading.Tasks;\n\npublic class LegacyFormSubmitter\n{\n    private readonly HttpClient _httpClient = new HttpClient();\n\n    public async Task SubmitFormDataAsync(Dictionary<string, string> formData)\n    {\n        var content = new FormUrlEncodedContent(formData);\n        var response = await _httpClient.PostAsync("https://legacywebsite.com/form", content);\n\n        if (response.IsSuccessStatusCode)\n        {\n            Console.WriteLine("Form submitted successfully.");\n        }\n        else\n        {\n            Console.WriteLine($"Failed to submit form. Status code: {response.StatusCode}");\n        }\n    }\n}\n\n// Usage example\npublic class Program\n{\n    public static async Task Main(string[] args)\n    {\n        var formData = new Dictionary<string, string>\n        {\n            { "field1", "value1" },\n            { "field2", "value2" }\n            // Add all the fields required by the legacy system here\n        };\n\n        var submitter = new LegacyFormSubmitter();\n        await submitter.SubmitFormDataAsync(formData);\n    }\n}