using System;\nusing System.ServiceModel;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        var pipeBinding = new NetNamedPipeBinding();\n        var channelFactory = new ChannelFactory<IInterProcessCommunication>(pipeBinding);\n        \n        // Ensure the URI matches the server endpoint with user-specific context if needed\n        string uri = "net.pipe://localhost/MyService/MyPipeName";\n        IInterProcessCommunication pipeProxy = channelFactory.CreateChannel(new EndpointAddress(uri));\n\n        try\n        {\n            pipeProxy.SendMessage("Hello from another instance!");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"Error: {ex.Message}");\n        }\n    }\n}