using System.Security.Policy;\n   using System.Web;\n\n   public class FullTrustChecker\n   {\n       public void Check()\n       {\n           try\n           {\n               new PermissionSet(PermissionState.Unrestricted).Demand();\n               HttpContext.Current.Response.Write("The application is running with full trust.");\n           }\n           catch (System.Security.SecurityException)\n           {\n               HttpContext.Current.Response.Write("The application is not running with full trust.");\n           }\n       }\n   }