using Microsoft.Web.WebView2.Wpf;\nusing System.Windows;\n\nnamespace HtmlEditor\n{\n    public partial class MainWindow : Window\n    {\n        public MainWindow()\n        {\n            InitializeComponent();\n            InitializeAsync();\n        }\n\n        async void InitializeAsync()\n        {\n            await webView.EnsureCoreWebView2Async(null);\n            webView.CoreWebView2.NavigateToString(\n                "<html><body contenteditable='true'>Edit this text.</body></html>");\n        }\n\n        private async void OnGetHtml(object sender, RoutedEventArgs e)\n        {\n            var html = await webView.ExecuteScriptAsync("document.body.outerHTML;");\n            string result = html.Substring(10, html.Length - 13).Replace("\\\"", "\"");\n            MessageBox.Show(result);\n        }\n    }\n}