using System;\nusing System.Linq;\n\npublic class Program\n{\n    public static void Main()\n    {\n        // Create the DataContext\n        using (var db = new MyDataContext())\n        {\n            // Example: Query to get all active users\n            var activeUsers = from user in db.Users\n                              where user.IsActive\n                              select user;\n\n            foreach (var user in activeUsers)\n            {\n                Console.WriteLine("Username: {0}, Email: {1}", user.Username, user.Email);\n            }\n        }\n    }\n}\n\n// Define the data context class generated by O/R Designer or manually created\npublic partial class MyDataContext : System.Data.Linq.DataContext\n{\n    public Table<User> Users;\n}\n\n// Example User entity\npublic class User\n{\n    public int UserId { get; set; }\n    public string Username { get; set; }\n    public string Email { get; set; }\n    public bool IsActive { get; set; }\n}