using (SqlConnection conn = new SqlConnection(connectionString))\n{\n    using (SqlCommand com = new SqlCommand("GetParentAndChildren", conn))\n    {\n        com.CommandType = CommandType.StoredProcedure;\n        com.Parameters.AddWithValue("@ParentID", someIdValue); // Replace with actual ID value\n\n        conn.Open();\n        using (SqlDataReader reader = com.ExecuteReader())\n        {\n            // Read results from the first SELECT statement\n            while (reader.Read())\n            {\n                // Process data from TableA\n                Console.WriteLine($"TableA Data: {reader["ColumnName"]}");\n            }\n\n            // Move to the next result set generated by the second SELECT statement\n            if (reader.NextResult())\n            {\n                while (reader.Read())\n                {\n                    // Process data from TableB\n                    Console.WriteLine($"TableB Data: {reader["ColumnName"]}");\n                }\n            }\n        }\n    }\n}