using System;\nusing System.Collections.Generic;\nusing System.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        List<List<string>> items = new List<List<string>>\n        {\n            "a|b|c|d|eeee|rere|ewew|".Split('|').Where(s => !string.IsNullOrEmpty(s)).ToList(),\n            "newqewq|ewew|".Split('|').Where(s => !string.IsNullOrEmpty(s)).ToList(),\n            "newew|ewewewewew|".Split('|').Where(s => !string.IsNullOrEmpty(s)).ToList(),\n            "a|b|c|d|eeee|rere".Split('|').Where(s => !string.IsNullOrEmpty(s)).ToList(),\n            "a|b|c|d|eeee".Split('|').Where(s => !string.IsNullOrEmpty(s)).ToList()\n        };\n\n        // Sort in ascending order\n        var sortedAscending = items.OrderBy(subList => subList.Count).ToList();\n\n        // Sort in descending order\n        var sortedDescending = items.OrderByDescending(subList => subList.Count).ToList();\n\n        Console.WriteLine("Sorted Ascending:");\n        foreach (var list in sortedAscending)\n        {\n            Console.WriteLine(string.Join("|", list) + "|");\n        }\n\n        Console.WriteLine("\nSorted Descending:");\n        foreach (var list in sortedDescending)\n        {\n            Console.WriteLine(string.Join("|", list) + "|");\n        }\n    }\n}