using System;\nusing System.ComponentModel;\nusing System.Windows.Forms;\n\npublic class Person {\n    private int _age;\n    \n    public int Age {\n        get { return _age; }\n        set {\n            if (value < 0 || value > 120) \n                throw new ArgumentOutOfRangeException(nameof(Age), "Age must be between 0 and 120.");\n            _age = value;\n        }\n    }\n}\n\npublic class MainForm : Form {\n    private TextBox ageTextBox;\n    private ErrorProvider errorProvider;\n\n    public MainForm() {\n        this.ageTextBox = new System.Windows.Forms.TextBox();\n        this.errorProvider = new System.Windows.Forms.ErrorProvider();\n        \n        ageTextBox.Location = new System.Drawing.Point(16, 16);\n        ageTextBox.Name = "ageTextBox";\n        ageTextBox.Size = new System.Drawing.Size(100, 20);\n        this.Controls.Add(ageTextBox);\n\n        Person person = new Person();\n        \n        Binding binding = new Binding("Text", person, "Age");\n        binding.Format += (sender, e) => {\n            if (e.Value == null || !int.TryParse(e.Value.ToString(), out int age))\n                errorProvider.SetError(ageTextBox, "Please enter a valid integer.");\n            else\n                errorProvider.Clear();                \n        };\n        binding.Parse += (sender, e) => {\n            if (!int.TryParse(e.Value?.ToString(), out int age)) {\n                e.Cancel = true;\n                errorProvider.SetError(ageTextBox, "Please enter a valid integer.");\n            } else {\n                person.Age = age;\n                errorProvider.Clear();\n            }\n        };\n\n        ageTextBox.DataBindings.Add(binding);\n    }\n\n    [STAThread]\n    static void Main() {\n        Application.EnableVisualStyles();\n        Application.Run(new MainForm());\n    }\n}