using System.Threading.Tasks;\n\n// Update the label text first\nlblStatus.Text = "Restoring Database";\n\n// Perform the restore operation in a task to keep the UI responsive\nTask.Run(() =>\n{\n    Restore fullRestore = new Restore();\n    \n    // Configure fullRestore\n\n    try\n    {\n        fullRestore.SqlRestore(_server);\n        fullRestore.Wait();\n\n        // Update UI after restoration is complete\n        this.Invoke((MethodInvoker)delegate()\n        {\n            lblStatus.Text = "Database Restored Successfully";\n        });\n    }\n    catch (Exception ex)\n    {\n        this.Invoke((MethodInvoker)delegate()\n        {\n            lblStatus.Text = $"Error: {ex.Message}";\n        });\n    }\n});