using System;\nusing System.Drawing;\nusing System.Drawing.Imaging;\n\nclass Program\n{\n    static void Main()\n    {\n        // Create a new Bitmap with specified width, height, and PixelFormat.\n        using (Bitmap bitmap = new Bitmap(100, 100, PixelFormat.Format8bppIndexed))\n        {\n            // For demonstration, fill the image with some colors.\n            ColorPalette palette = bitmap.Palette;\n            for (int i = 0; i < 256; i++)\n                palette.Entries[i] = Color.FromArgb(i * 255 / 255, i * 187 / 255, i * 135 / 255);\n\n            bitmap.Palette = palette;\n\n            // Set pixel values\n            for (int y = 0; y < bitmap.Height; y++)\n                for (int x = 0; x < bitmap.Width; x++)\n                    bitmap.SetPixel(x, y, Color.FromArgb(255 * x / bitmap.Width, 255 * y / bitmap.Height, 127));\n\n            // Save the Bitmap as a PNG file.\n            bitmap.Save("output_8bpp.png", ImageFormat.Png);\n        }\n    }\n}