using System;\nusing System.Security.Cryptography;\n\nclass PasswordStorage\n{\n    public static byte[] ProtectData(byte[] userData)\n    {\n        return ProtectedData.Protect(userData, null, DataProtectionScope.CurrentUser);\n    }\n\n    public static byte[] UnprotectData(byte[] encryptedData)\n    {\n        return ProtectedData.Unprotect(encryptedData, null, DataProtectionScope.CurrentUser);\n    }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        string password = "MySecretPassword";\n        byte[] data = System.Text.Encoding.UTF8.GetBytes(password);\n\n        // Encrypt data\n        byte[] encryptedData = PasswordStorage.ProtectData(data);\n        Console.WriteLine("Encrypted: {0}", Convert.ToBase64String(encryptedData));\n\n        // Decrypt data\n        byte[] decryptedData = PasswordStorage.UnprotectData(encryptedData);\n        string decryptedPassword = System.Text.Encoding.UTF8.GetString(decryptedData);\n        Console.WriteLine("Decrypted: {0}", decryptedPassword);\n    }\n}