using System;\nusing System.Collections.Generic;\nusing System.Linq;\nusing System.Diagnostics;\n\npublic class AppTracker {\n    public static List<string> GetCurrentProcesses() {\n        var processList = new List<string>();\n        foreach (Process process in Process.GetProcesses()) {\n            processList.Add(process.ProcessName);\n        }\n        return processList;\n    }\n\n    public static void Main(string[] args) {\n        while (true) {\n            var processes = GetCurrentProcesses();\n            Console.WriteLine("Running processes: " + string.Join(", ", processes));\n            System.Threading.Thread.Sleep(900000); // Sleep for 15 minutes\n        }\n    }\n}