using System;\nusing System.Collections.Generic;\nusing System.IdentityModel.Tokens.Jwt;\nusing Microsoft.IdentityModel.Tokens;\n\npublic class JwtManager {\n    private readonly string _secretKey = "your_secret_key";\n\n    public string GenerateToken(string userId) {\n        var securityKey = new SymmetricSecurityKey(System.Text.Encoding.UTF8.GetBytes(_secretKey));\n        var credentials = new SigningCredentials(securityKey, SecurityAlgorithms.HmacSha256);\n\n        var claims = new[] { \n            new System.Security.Claims.Claim(JwtRegisteredClaimNames.Sub, userId),\n            new System.Security.Claims.Claim(JwtRegisteredClaimNames.Jti, Guid.NewGuid().ToString()) };\n\n        var token = new JwtSecurityToken(\n            issuer: "your_issuer",\n            audience: "your_audience",\n            claims,\n            expires: DateTime.Now.AddMinutes(30),\n            signingCredentials: credentials);\n\n        return new JwtSecurityTokenHandler().WriteToken(token);\n    }\n}