protected override void OnInit(EventArgs e)\n{\n    base.OnInit(e);\n    priceTextbox.ID = this.ID + "_price";\n    quantityTextbox.ID = this.ID + "_quantity";\n    timeTextbox.ID = this.ID + "_time";\n    submitButton.ID = this.ID + "_submit";\n    submitButton.Text = "Submit";\n\n    priceMask.TargetControlID = priceTextbox.ClientID;\n    priceMask.ID = priceMask.TargetControlID + "_extender";\n    priceMask.BehaviorID = "priceMaskExtender";\n    priceMask.MaskType = MaskedEditType.Number; // Set the mask type to Number\n    priceMask.Mask = "99.99";\n    priceMask.DisplayMoney = MaskedEditShowSymbol.Left;\n\n    // Optionally, you can set other properties like AcceptNegative\n}