using System;\n\n   class Program\n   {\n       static void Main(string[] args)\n       {\n           if (args.Length > 0)\n           {\n               string method = args[0];\n               switch (method)\n               {\n                   case "Method1":\n                       // Assuming Method1 returns a string\n                       Console.WriteLine(Method1());\n                       break;\n                   case "Method2":\n                       // Assuming Method2 takes an integer and returns a string\n                       int value = Convert.ToInt32(args[1]);\n                       Console.WriteLine(Method2(value));\n                       break;\n               }\n           }\n       }\n\n       static string Method1()\n       {\n           return "Result from Method1";\n       }\n\n       static string Method2(int value)\n       {\n           return $"Result {value * 2} from Method2";\n       }\n   }