using DevExpress.XtraGrid.Views.Grid;\n\nprivate void InitializeGrid()\n{\n    // Create and configure the GridControl and GridView\n    GridControl gridControl = new GridControl();\n    GridView gridView = new GridView(gridControl);\n\n    gridControl.MainView = gridView;\n    gridControl.Dock = DockStyle.Fill;\n\n    // Add columns\n    gridView.Columns.Add(new DevExpress.XtraGrid.Columns.GridColumn() { FieldName = "Description", Caption = "Description" });\n\n    // Enable multi-line rows\n    gridView.OptionsView.AllowHtmlDrawHeaders = true;\n    gridView.OptionsView.EnableAppearanceEvenRow = true;\n    gridView.Appearance.Row.Font = new System.Drawing.Font("Tahoma", 8);\n    \n    // Populate with data\n    gridControl.DataSource = GetDataSource();\n\n    this.Controls.Add(gridControl);\n}\n\nprivate DataTable GetDataSource()\n{\n    DataTable table = new DataTable();\n    table.Columns.Add("Description", typeof(string));\n    table.Rows.Add(\n        "This is a detailed\ndescription on multiple lines.\nIt demonstrates how multi-line rows work.");\n    return table;\n}