using System;\nusing System.ServiceModel;\n\nclass Program\n{\n    static void Main()\n    {\n        using (ServiceHost host = new ServiceHost(typeof(MetaWeblogService), new Uri("http://localhost:8080/MetaWeblog")))\n        {\n            try\n            {\n                host.AddServiceEndpoint(typeof(IMetaWeblog), new BasicHttpBinding(), "");\n                host.Open();\n                Console.WriteLine("The service is up and running.");\n                Console.ReadLine();\n            }\n            catch (Exception e)\n            {\n                Console.WriteLine($"Error: {e.Message}");\n            }\n        }\n    }\n}